import React from 'react'

function Basket(){
    let basketProduct = JSON.parse(localStorage.getItem('basket')) || []
    
    return(
        <div className='wrapper_basket'>
        <h1>Моя корзина</h1>
        <hr />
       {basketProduct.map(item=>{
           return(
            <div className = "basket_item">
                <h2>{item.title}</h2>
                <img src={`.${item.image}`}/>
                <p>{item.price} <b>РУБ</b></p>
                <input>{item.count}</input>
                <p>{item.description}</p>
                <button className='btn_delete material-icons'>X</button> 
           </div>
            )
        })}
       </div>
    )
}

export default Basket
