import React,{useState,useEffect} from 'react'
import '../style/style.css'

function Basket(){
    const [basket,setBasket] = useState([])
 
    useEffect(()=>{
        const basketProduct = JSON.parse( localStorage.getItem('basket') || [])
         setBasket(basketProduct)
      
      },[])

      useEffect(()=>{
        localStorage.setItem('basket' ,JSON.stringify(basket))
      },[basket])



    const removeItem=(id)=>{
        setBasket(prev => prev.filter(item => item.id !== id))
      
    }

    const[title_todo, setTitle_todo]  = useState('')

    const changeHandler = (e)=>{
      setTitle_todo(e.target.value)
  
    }
     const keyPressHandler = (e)=>{
       if(e.key === 'Enter'){
        console.log(e.target.value)
  
       }
  
     }
  
  
   
    
    return(
        <div className='wrapper_basket'>
        <h1>Моя корзина</h1>
        <hr />
       {basket.map(item=>{
           return(
            <div className = "basket_item">
                <img src={`.${item.image}`}/>
                <h2>{item.title}</h2>
                <p>{item.price} <b>РУБ</b></p>
                <input 
                type="text"
                 value={item.count}  
                 onChange={changeHandler}
                 onKeyPress={keyPressHandler} / >
                <p>{item.description}</p>
                <button className='btn_delete material-icons' onClick={()=> removeItem(item.id)}>X</button> 
           </div>
            )
        })}
       </div>
    )
}

export default Basket
