import React,{useState,useEffect} from 'react'
import '../style/style.css'

function Basket(){
  
    const [basket,setBasket] = useState([])
 
    useEffect(()=>{
        const basketProduct = JSON.parse( localStorage.getItem('basket') || [])
         setBasket(basketProduct)
      
      },[])

      useEffect(()=>{
        localStorage.setItem('basket' ,JSON.stringify(basket))
      },[basket])



    const removeItem=(id)=>{
        setBasket(prev => prev.filter(item => item.id !== id))
       
      
    }

   const totalSumm=(...args)=>{
        let summ = 0;
        setBasket(prev => prev.forEach((item) => {
           summ += item[summ]}))
       
           return summ
    }
    
    /* const totalPrice = (arr) =>{
        let sum = 0;
        for(var i = 0; i < arr.length; i++){
            sum += arr[i];
        }
        return sum
    }*/
    
 /* function getCount(){
    if(basketProduct == 0) return basketProduct;
    let count = 0;

    basketProduct.forEach((product) => {
      count += product.count
    });

    return count
}*/

    


    if(basket.length === 0){
        return(
         <div className='wrapper_basket'>
            <h1>Корзина пуста</h1>
            <hr />
         
         </div>
        )
    }

    
    
    return(
        <div className='wrapper_basket'>
        <h1>Моя корзина</h1>
        <hr />
       {basket.map(item=>{

         return(
            
            <div className = "basket_item_wrapper" key={item.id}>
                <div>
                    <img src={`.${item.image}`}/>
                    <h2 className = "basket_item_title">{item.title}</h2>
                </div>
                <div className = "basket_item_wrapper2">
                    <p>Цена: {item.price}  Руб </p>
                    <div>Количество: {item.count}</div>
                    <hr />
                    <div>Стоимость: {item.summ = item.count * item.price} Руб </div> 
                    <button className='btn_delete' onClick={()=> removeItem(item.id)}>DELETE</button>
                   
                </div>
                <div  className='total_price'>{totalSumm(item.summ)}</div> 
            </div>
            
        )
        })}
            <hr />
           {basket.forEach(item=>{
               return(
                   <>
               
           
                <button className='btn_delete' >DELETE</button>
</>
               )
           })} 
            
       </div>
    )
}

export default Basket
