import React from 'react'
import {NavLink, useLocation} from 'react-router-dom'
import Search from './Search'

function Header(){
  const basketProduct = JSON.parse(localStorage.getItem('basket')) || 0
  const location = useLocation()

  function getCount(){
      if(basketProduct == 0) return basketProduct;
      let count = 0;

      basketProduct.forEach((product) => {
        count += product.count
      });

      return count



  }



    return(
        <header className="header">
          <div className="header_logo"><NavLink to="/">VivaBride</NavLink></div>
          <div className="header_widget">
            {location.pathname == '/basket' ? <span>в корзине :{getCount()}</span>:
            <>
             <Search />
              <div className="header_basket">
                <NavLink to="/basket"><img src="./img/basket.svg" /><span className="header_basket_count">{getCount()}</span></NavLink>
              </div>
            </> 
            
            }
             
          </div>
      </header>
    )
}

export default Header
