import React,{useState, useEffect} from 'react'


const images= [
  <img src={'../img/slider/1.jpg'} key='slide1'/>,
  <img src={'../img/slider/2.jpg'} key='slide2'/>,
  <img src={'../img/slider/3.jpg'} key='slide3'/>,
  <img src={'../img/slider/4.jpg'} key='slide4'/>,
  <img src={'../img/slider/5.jpg'} key='slide5'/>,
  <img src={'../img/slider/6.jpg'} key='slide6'/>,
  <img src={'../img/slider/7.jpg'} key='slide7'/>,
  <img src={'../img/slider/8.jpg'} key='slide8'/>,
  <img src={'../img/slider/9.jpg'} key='slide9'/>,
  <img src={'../img/slider/10.jpg'} key='slide10'/>,
  <img src={'../img/slider/11.jpg'} key='slide11'/>,
  <img src={'../img/slider/12.jpg'} key='slide12'/>,
  <img src={'../img/slider/13.jpg'} key='slide13'/>,
  <img src={'../img/slider/14.jpg'} key='slide14'/>
]


function Slider(){
  const [activeIndex, setActiveIndex] = useState(0)

  useEffect(()=>{
    const interval = setInterval (()=>{
      setActiveIndex((current) => {
        current === images.length - 1 ? 0 : activeIndex + 1
      },1000)
      return ()=> clearInterval()

    })


  },[])

   const prevIndex = activeIndex ? activeIndex-1 : images.length - 1
   const nextIndex = activeIndex === images.length - 1 ? 0 :activeIndex + 1


  return(
    <div className="slider_wrapper">
      <div className="slide  slide_prev" key= {prevIndex}>{images[prevIndex]}</div>
      <div className="slide" key= {activeIndex}> {images[activeIndex]}</div>
      <div className="slide slide_next"  key= {nextIndex}> {images[nextIndex]}</div>
    </div>
  )

}
export default Slider
