import React,{useState,useEffect} from 'react'
import '../style/style.css'

function Basket(){
    const [basket,setBasket] = useState([])
 
    useEffect(()=>{
        const basketProduct = JSON.parse( localStorage.getItem('basket') || [])
         setBasket(basketProduct)
      
      },[])

      useEffect(()=>{
        localStorage.setItem('basket' ,JSON.stringify(basket))
      },[basket])



    const removeItem=(id)=>{
        setBasket(prev => prev.filter(item => item.id !== id))
      
    }
  

    if(basket.length === 0){
        return(
         <div className='wrapper_basket'>
            <h1>Моя корзина</h1>
            <hr />
          <p> Здесь пока ничего нет </p>
         </div>
        )
    }

    
    
    return(
        <div className='wrapper_basket'>
        <h1>Моя корзина</h1>
        <hr />
       {basket.map(item=>{

           return(
            <div className = "basket_item_wrapper">
                
                <img src={`.${item.image}`}/>
                <h2>{item.title}</h2>
                <p>{item.price} <b>РУБ</b></p>
                <div>{item.count}</div>
                <p>{item.description}</p>
                <button className='btn_delete material-icons' onClick={()=> removeItem(item.id)}>X</button> 
           </div>
            )
        })}
       </div>
    )
}

export default Basket
